import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './style.css';

const CustomerDashboard = () => {
  const [showButtons, setShowButtons] = useState(false); // State to control showing buttons
  const history = useNavigate();

  const handleDashboardClick = () => {
    setShowButtons(true); // Show buttons when the dashboard button is clicked
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <h4 align='center' className="name">Customer</h4>
          <li><button onClick={handleDashboardClick}>Dashboard</button></li>
          <li><button>Orders</button></li>
          <li><button>Settings</button></li>
          <li><button>Logout</button></li>
        </ul>
      </div>
      
      <div className="container">
        <div className="customer-details">
          {showButtons && (
            <div>
              <h2>Dashboard</h2><br/>
              <div className="row">
                {/* Render your buttons here */}
                <button>Orders</button>
                <button>Account Details</button>
                <button>Wishlist</button>
                <button>Logout</button>
                <button>Recommendations</button>
                <button>Production Status</button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
