import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
    const [selectedCustomer, setSelectedCustomer] = useState(null);

    useEffect(() => {
        // Fetch details of the logged-in customer when component mounts
        axios.get('http://Localhost:5000/users/userDetails', { withCredentials: true })
            .then(response => {
                setSelectedCustomer(response.data.user);
            })
            .catch(error => {
                console.error('Error fetching customer details:', error);
            });
    }, []);

    return (
        <div className="dashboard">
            <div className="sidebar">
                <ul>
                    <li>Dashboard</li>
                    <li>Orders</li>
                    <li>Profile</li>
                    <li>Settings</li>
                    <li>Logout</li>
                </ul>
            </div>
            <div className="container">
                <h1>Welcome to the Customer Dashboard!</h1>
                <h2>Customer Details</h2>
                {selectedCustomer ? (
                    <div>
                        <p><strong>Name:</strong> {selectedCustomer.name}</p>
                        <p><strong>Email:</strong> {selectedCustomer.email}</p>
                        <p><strong>Address:</strong> {selectedCustomer.address}</p>
                        {/* Add more customer details as needed */}
                    </div>
                ) : (
                    <p>Loading customer details...</p>
                )}
            </div>
        </div>
    );
};

export default CustomerDashboard;
