import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [user, setUser] = useState(null);

  const fetchCustomerDetails = async (customerId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data.user);
    } catch (error) {
      console.error('Error fetching customer details:', error);
    }
  };

  // Call fetchCustomerDetails function with logged-in user's ID when component mounts
  useEffect(() => {
    const customerId = localStorage.getItem('id');
    fetchCustomerDetails(customerId);
  }, []);

  // Function to handle click event on Profile item in sidebar
  const handleProfileClick = () => {
    const customerId = localStorage.getItem('id');
    fetchCustomerDetails(customerId);
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li>Dashboard</li>
          <li>Orders</li>
          <li onClick={handleProfileClick}>Profile</li> {/* Add onClick event handler */}
          <li>Settings</li>
          <li>Logout</li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome to the Customer Dashboard!</h1>
        <h2>Customer Details</h2>
        {user ? (
          <div>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>DOB:</strong> {user.DOB}</p>
            <p><strong>Address:</strong> {user.address}</p>
            <p><strong>Phone Number:</strong> {user.phoneNumber}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Username:</strong> {user.username}</p>
            {/* Add more customer details as needed */}
          </div>
        ) : (
          <p>Loading customer details...</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
