import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './style.css';
import axios from 'axios';

import { useDispatch, useSelector } from "react-redux";
import { signout } from '../../../actions/user.action';

const CustomerDashboard = () => {
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [headerTitle, setHeaderTitle] = useState("Dashboard"); // State to store header title
  const history = useNavigate();

  const auth = useSelector(state => state.auth);
  const dispatch = useDispatch();

  const logout = () => {
    dispatch(signout());
  }

  const fetchCustomerDetails = async () => {
    try {
      const token = window.localStorage.getItem("token");
      const customerId = localStorage.getItem('id');
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data.user);
      setUpdatedUser(response.data.user);
    } catch (error) {
      if (error.response && error.response.status === 401) {
        console.error('Token expired or unauthorized:', error.response);
        alert('Your session has expired. Please login again.');
        dispatch(signout());
      } else {
        console.error('Error fetching customer details:', error.response);
      }
    }
  };

  const handleButtonClick = (title) => {
    setHeaderTitle(title); // Update header title when a button is clicked
    if (title === "Profile") {
      fetchCustomerDetails(); // Fetch customer details when Profile button is clicked
    }
  };

  const handleUpdate = async () => {
    try {
      const token = window.localStorage.getItem("token");
      const customerId = user._id.toString();
      await axios.put(`http://localhost:5000/users/${customerId}`, updatedUser, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      await fetchCustomerDetails();
      setIsEditing(false);
    } catch (error) {
      if (error.response && error.response.status === 401) {
        console.error('Token expired or unauthorized:', error.response);
        alert('Your session has expired. Please login again.');
        dispatch(signout());
      } else {
        console.error('Error updating customer details:', error.response);
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedUser({ ...updatedUser, [name]: value });
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <h4 align='center' className="name">{user ? user.name : 'Customer'}</h4>
          <li><button onClick={() => handleButtonClick("Dashboard")}>Dashboard</button></li>
          <li><button onClick={() => handleButtonClick("Orders")}>Orders</button></li>
          <li><button onClick={() => handleButtonClick("Profile")}>Profile</button></li>
          <li><button onClick={() => handleButtonClick("Settings")}>Settings</button></li>
          <li><button onClick={logout}>Logout</button></li>
        </ul>
      </div>
      <div className="container">
        <div className="customer-details">
          <h2>{headerTitle}</h2>
          {user && (
            <div>
              {/* Render profile details when Profile button is clicked */}
              {headerTitle === "Profile" && (
                <div>
                  <div className="detail">
                    <label>Name:</label>
                    {isEditing ? <input type="text" name="name" value={updatedUser.name} onChange={handleChange} /> : <span>{user.name}</span>}
                  </div>
                  <div className="detail">
                    <label>DOB:</label>
                    {isEditing ? <input type="text" name="DOB" value={updatedUser.DOB} onChange={handleChange} /> : <span>{user.DOB}</span>}
                  </div>
                  <div className="detail">
                    <label>Address:</label>
                    {isEditing ? <input type="text" name="address" value={updatedUser.address} onChange={handleChange} /> : <span>{user.address}</span>}
                  </div>
                  <div className="detail">
                    <label>PhoneNumber:</label>
                    {isEditing ? <input type="text" name="phoneNumber" value={updatedUser.phoneNumber} onChange={handleChange} /> : <span>{user.phoneNumber}</span>}
                  </div>
                  <div className="detail">
                    <label>Email:</label>
                    {isEditing ? <input type="text" name="email" value={updatedUser.email} onChange={handleChange} /> : <span>{user.email}</span>}
                  </div>
                  <div className="detail">
                    <label>Username:</label>
                    {isEditing ? <input type="text" name="username" value={updatedUser.username} onChange={handleChange} /> : <span>{user.username}</span>}
                  </div>

                  {isEditing ? (
                    <button className="saveButton" onClick={handleUpdate}>Save</button>
                  ) : (
                    <button className="editButton" onClick={() => setIsEditing(true)}>Edit</button>
                  )}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
