import React, { useState } from 'react';
import './style.css';
import { useDispatch } from 'react-redux';
import { login } from '../../actions';
import { useNavigate } from 'react-router-dom'; // Import useHistory hook

const Header = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const dispatch = useDispatch();
  const history = useNavigate (); // Initialize useHistory hook

  const handleLoginButtonClick = () => {
    setShowLoginForm(true);
  };

  const handleAdminLoginButtonClick = () => {
    // Redirect user to admin page running on port 4000
    window.location.href = 'http://localhost:4000/signin';
  };

  const userLogin = () => {
    // Retrieve email and password values from the input fields
    const email = document.getElementById('email').value;
    const password = document.getElementById('password').value;
    const role = document.getElementById('role').value;

    // Dispatch login action with email and password
    dispatch(login({ email, password,role }));
 if (role === 'customer') {
    // Redirect to the customer dashboard upon successful login
    history('/customer/dashboard');
   
      setShowLoginForm(false);
 }
  };

  return (
    <div>
      <div className='headerBar'>
        <div className='spacer' /> {/* Spacer to push buttons to the right */}
        <button className='adminLoginButton' onClick={handleAdminLoginButtonClick}>
          Admin Login
        </button>
        <div style={{ marginRight: '10px' }} /> {/* Add space between buttons */}
        <button className='loginButton' onClick={handleLoginButtonClick}>
          Customer Login
        </button>
      </div>
      {showLoginForm && (
        <div className='loginFormContainer'>
          <div className='loginForm'>
            <h2>Login</h2>
            <input type='email' id='email' placeholder='email' className='inputField' />
            <input type='password' id='password' placeholder='Password' className='inputField' />
            <button className='loginSubmitButton' onClick={userLogin}>Login</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Header;
