import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './components/Header';
import CustomerDashboard from './containers/HomePage/CustomerDashboard'; // Import CustomerDashboard component
import LoginForm from './containers/HomePage/LoginForm'; // Import LoginForm component
import { useDispatch, useSelector } from "react-redux";
import { isUserLoggedIn } from './actions';

function App() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);
  const token = window.localStorage.getItem('token');

  useEffect(() => {
    if (!auth.authenticate) {
      dispatch(isUserLoggedIn());
    }
  }, [dispatch, auth.authenticate]);

  return (
    <Router>
      <div className="App">
        <Header />
        <Routes>
          {/* Display login form if not authenticated */}
          {!auth.authenticate && <Route path="/" element={<LoginForm />} />}

          {/* Redirect to CustomerDashboard if authenticated */}
          {auth.authenticate && <Route path="/" element={<Navigate to="/customer/dashboard" />} />}

          {/* Customer dashboard route */}
          <Route path="/customer/dashboard" element={<CustomerDashboard />} />

          {/* Other routes */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
