import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './style.css';
import axios from 'axios';
import { useDispatch, useSelector } from "react-redux";
import { signout } from '../../../actions/user.action';
import { refreshToken } from '../../../utils/auth'; // Import refreshToken function

const CustomerDashboard = () => {
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const history = useNavigate();

  const auth = useSelector(state => state.auth);
  const dispatch = useDispatch();

  const logout = () => {
    dispatch(signout());
  }

  const fetchCustomerDetails = async () => {
    try {
      await refreshToken(); // Refresh token before fetching customer details
      const token = window.localStorage.getItem("token");
      const customerId = localStorage.getItem('id');
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data.user);
      setUpdatedUser(response.data.user);
    } catch (error) {
      console.error('Error fetching customer details:', error.response);
      if (error.response.status === 401) {
        logout(); // Logout user if token refresh fails or user is unauthorized
        history('/login'); // Redirect to login page
      }
    }
  };

  // Other functions (handleUpdate, handleChange) remain unchanged

  return (
    <div className="dashboard">
      {/* Dashboard content */}
    </div>
  );
};

export default CustomerDashboard;
