import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [customer, setCustomer] = useState(null);

  // Function to fetch details of the logged-in customer
  const fetchCustomerDetails = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/users/me', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const userId = response.data.user._id; // Assuming user ID is available in the response
      const userDetailsResponse = await axios.get(`http://localhost:5000/users/${userId}`);
      setCustomer(userDetailsResponse.data.user);
    } catch (error) {
      console.error('Error fetching customer details:', error);
    }
  };

  // Call fetchCustomerDetails function when the component mounts
  useEffect(() => {
    fetchCustomerDetails();
  }, []);

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li>Dashboard</li>
          <li>Orders</li>
          <li>Profile</li>
          <li>Settings</li>
          <li>Logout</li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome to the Customer Dashboard!</h1>
        <h2>Customer Details</h2>
        {customer ? (
          <div>
            <p><strong>Name:</strong> {customer.name}</p>
            <p><strong>Email:</strong> {customer.email}</p>
            <p><strong>Address:</strong> {customer.address}</p>
            {/* Add more customer details as needed */}
          </div>
        ) : (
          <p>Loading customer details...</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
fetchCustomerDetails();
