import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './components/Header';
import CustomerDashboard from './containers/HomePage/CustomerDashboard';
import { useDispatch, useSelector } from "react-redux";
import { isUserLoggedIn } from './actions';

function App() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();

  useEffect(() => {
    dispatch(isUserLoggedIn());
  }, [dispatch]);

  useEffect(() => {
    setIsLoggedIn(auth.authenticate);
  }, [auth.authenticate]);

  return (
    <Router>
      <div className="App">
        {/* Render the Header only if isLoggedIn is true and the current location is not the customer dashboard */}
        {isLoggedIn && window.location.pathname !== '/customer/dashboard' && <Header />}
        <Routes>
        {(isLoggedIn && location.pathname !== '/customer/dashboard') && <Header />} {/* Use location.pathname */}
          <Route
            path="/"
            element={isLoggedIn ? <Navigate to="/" /> : <CustomerDashboard />}
          />
          <Route
            path="/customer/dashboard"
            element={isLoggedIn ? <CustomerDashboard /> : <Navigate to="/" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
