import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './components/Header';
import CustomerDashboard from './containers/HomePage/CustomerDashboard'; // Import CustomerDashboard component

import { useDispatch, useSelector } from "react-redux";
import { isUserLoggedIn } from './actions';

function App() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    dispatch(isUserLoggedIn());
  }, [dispatch]);

  useEffect(() => {
    setIsLoggedIn(auth.authenticate);
    setUserRole(auth.user ? auth.user.role : '');
  }, [auth.authenticate, auth.user]);

  return (
    <Router>
      <div className="App">
        {/* Conditionally render Header based on isLoggedIn and userRole */}
        {isLoggedIn && userRole === 'customer' && window.location.pathname !== '/customer/dashboard' && <Header />}
        <Routes>
          <Route path="/" element={<Navigate to="/customer/dashboard" />} />
          <Route path="/customer/dashboard" element={<CustomerDashboard />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
