// LoginForm.js
import React, { useState } from 'react';
import { useNavigate} from 'react-router-dom'; // Import useHistory hook to handle redirection

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history =useNavigate(); // Initialize useHistory hook

  const handleSubmit = (e) => {
    e.preventDefault();
    // Assuming validation logic here and dispatching login action

    // Assuming the login is successful
    // Redirect the user to the customer dashboard
    history.push('/customer/dashboard'); // Redirect to the customer dashboard route
  };

  return (
    <div className='loginFormContainer'>
      <form className='loginForm' onSubmit={handleSubmit}>
        <h2>Login</h2>
        <input 
          type='email' 
          id='email' 
          placeholder='email' 
          className='inputField' 
          value={email}
          onChange={(e) => setEmail(e.target.value)} 
        />
        <input 
          type='password' 
          id='password' 
          placeholder='Password' 
          className='inputField' 
          value={password}
          onChange={(e) => setPassword(e.target.value)} 
        />
        <button type='submit' className='loginSubmitButton'>Login</button>
      </form>
    </div>
  );
};

export default LoginForm;
