import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [user, setCustomer] = useState(null);

  const fetchCustomerDetails = async () => {
    try {
      const token = localStorage.getItem('token');
      const customerId = localStorage.getItem('id'); // Get the customer ID from localStorage
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setCustomer(response.data.user);
    } catch (error) {
      console.error('Error fetching customer details:', error);
    }
  };


 // Define a function to handle user updates
const handleUpdate = async () => {
  try {
    const token = localStorage.getItem('token');
    // Update user object with new information
    const updatedUser = { ...user, name: 'New Name', email: 'newemail@example.com' }; // Replace with actual updated data
    
    // Send request to update user information
    const response = await axios.put(`http://localhost:5000/users/${user._id}`, updatedUser, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });

    // Update state with the response from the server if necessary
    setCustomer(updatedUser); // Update user state with updatedUser
    
    console.log(response.data); // Log response from the server
    // Optionally, you can update the state or show a success message
  } catch (error) {
    console.error('Error updating user:', error);
    // Handle error: show error message or perform other actions
  }
};

  
  // Handler for deleting user information
  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.delete(`http://localhost:5000/users/${user._id}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      console.log(response.data); // Log response from the server
      // Optionally, you can update the state or show a success message
    } catch (error) {
      console.error('Error deleting user:', error);
      // Handle error: show error message or perform other actions
    }
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li><button>Dashboard</button></li>
          <li><button>Orders</button></li>
          <li><button onClick={fetchCustomerDetails}>Profile</button></li>
          <li><button>Settings</button></li>
          <li><button>Logout</button></li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome, {user ? user.name : 'Customer'}!</h1>
        <h2>My Profile</h2>
        {user ? (
          <div>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>DOB:</strong> {user.DOB}</p>
            <p><strong>Address:</strong> {user.address}</p>
            <p><strong>Phone Number:</strong> {user.phoneNumber}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Username:</strong> {user.username}</p>
            {/* Add more customer details as needed */}
            <button onClick={handleUpdate}>Update</button>
            <button onClick={handleDelete}>Delete</button>
          </div>
        ) : (
          <p>Loading customer details...</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
