// CustomerDashboard.js
import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [selectedCustomerId, setSelectedCustomerId] = useState(null);
  const [selectedCustomer, setSelectedCustomer] = useState(null);

  useEffect(() => {
    // Fetch all customers when component mounts
    axios.get('http://Localhost:5000/users')
      .then(response => {
        setCustomers(response.data.users); // Assuming the response contains an array of users
      })
      .catch(error => {
        console.error('Error fetching customers:', error);
      });
  }, []);

  useEffect(() => {
    // Fetch selected customer details when selectedCustomerId changes
    if (selectedCustomerId) {
      axios.get(`http://Localhost:5000/users/${selectedCustomerId}`)
        .then(response => {
          setSelectedCustomer(response.data.user);
        })
        .catch(error => {
          console.error('Error fetching customer details:', error);
        });
    }
  }, [selectedCustomerId]);

  const handleCustomerClick = (customerId) => {
    setSelectedCustomerId(customerId);
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li>Dashboard</li>
          <li>Orders</li>
          <li>Profile</li>
          <li>Settings</li>
          <li>Logout</li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome to the Customer Dashboard!</h1>
        <h2>Customers</h2>
        <ul>
          {customers.map(customer => (
            <li key={customer._id} onClick={() => handleCustomerClick(customer._id)}>
              {customer.name}
            </li>
          ))}
        </ul>
        <h2>Customer Details</h2>
        {selectedCustomer ? (
          <div>
            <p><strong>Name:</strong> {selectedCustomer.name}</p>
            <p><strong>Email:</strong> {selectedCustomer.email}</p>
            <p><strong>Address:</strong> {selectedCustomer.address}</p>
            {/* Add more customer details as needed */}
          </div>
        ) : (
          <p>Select a customer to view details</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
