// CustomerDashboard.js

import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [user, setCustomer] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);

  useEffect(() => {
    fetchCustomerDetails();
  }, []);

  const fetchCustomerDetails = async () => {
    try {
      const token = localStorage.getItem('token');
      const customerId = localStorage.getItem('id');
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setCustomer(response.data.user);
      setUpdatedUser(response.data.user); // Set updatedUser with initial user data
    } catch (error) {
      console.error('Error fetching customer details:', error);
    }
  };

  const handleUpdate = async () => {
    try {
      const token = localStorage.getItem('token');
      const customerId = localStorage.getItem('id');
      await axios.put(`http://localhost:5000/users/${customerId}`, updatedUser, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      fetchCustomerDetails();
    } catch (error) {
      console.error('Error updating customer details:', error);
    }
  };

  // Update the state when input fields change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedUser({ ...updatedUser, [name]: value });
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li><button>Dashboard</button></li>
          <li><button>Orders</button></li>
          <li><button onClick={fetchCustomerDetails}>Profile</button></li>
          <li><button>Settings</button></li>
          <li><button>Logout</button></li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome, {user ? user.name : 'Customer'}!</h1>
        <h2>My Profile</h2>
        {user ? (
          <div>
            <p><strong>Name:</strong> <input type="text" name="name" value={updatedUser.name} onChange={handleInputChange} /></p>
            <p><strong>DOB:</strong> <input type="text" name="DOB" value={updatedUser.DOB} onChange={handleInputChange} /></p>
            <p><strong>Address:</strong> <input type="text" name="address" value={updatedUser.address} onChange={handleInputChange} /></p>
            <p><strong>Phone Number:</strong> <input type="text" name="phoneNumber" value={updatedUser.phoneNumber} onChange={handleInputChange} /></p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Username:</strong> {user.username}</p>
            <button onClick={handleUpdate}>Save</button>
            {/* Add more customer details as needed */}
          </div>
        ) : (
          <p>Loading customer details...</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
