import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import Header from './components/Header';
import CustomerDashboard from './containers/HomePage/CustomerDashboard'; // Import CustomerDashboard component

import { useDispatch, useSelector } from "react-redux";
import { isUserLoggedIn } from './actions';

function App() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    dispatch(isUserLoggedIn());
  }, [dispatch]);

  useEffect(() => {
    setIsLoggedIn(auth.authenticate);
  }, [auth.authenticate]);

  return (
    <Router>
      <div className="App">
        <Header />
        <Routes>
          <Route path="/" element={<Header isLoggedIn={isLoggedIn} />} />
          <Route path="/customer/dashboard" element={<CustomerDashboard />} />
        </Routes>
      </div>
    </Router>
  );
}

function Home({ isLoggedIn }) {
  const location = useLocation();

  useEffect(() => {
    if (isLoggedIn && location.pathname === '/') {
      // Redirect to customer dashboard if the user is logged in and on the root path
      Navigate('/customer/dashboard');
    }
  }, [isLoggedIn, location.pathname]);

  return null; // Return null to avoid rendering anything for the home route
}

export default App;
