import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import Header from './components/Header';
import CustomerDashboard from './containers/HomePage/CustomerDashboard'; // Import CustomerDashboard component
import { useDispatch, useSelector } from "react-redux";
import { isUserLoggedIn } from './actions';

function App() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();

  useEffect(() => {
    dispatch(isUserLoggedIn());
  }, [dispatch]);

  useEffect(() => {
    setIsLoggedIn(auth.authenticate);
  }, [auth.authenticate]);

  return (
      <div className="App">
        {/* Render the Header component */}
        {isLoggedIn && location.pathname !== '/customer/dashboard' && <Header />}
        {/* Define Routes */}
        <Routes>
          {/* Route for the root path */}
          <Route path="/" element={!isLoggedIn ? <Header /> : <Navigate to="/customer/dashboard" />} />
          {/* Route for the customer dashboard */}
          <Route path="/customer/dashboard" element={isLoggedIn ? <CustomerDashboard /> : <Navigate to="/" />} />
        </Routes>
      </div>

  );
}

export default App;
