import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './components/Header';
import CustomerDashboard from './containers/HomePage/CustomerDashboard'; // Import CustomerDashboard component

import { useDispatch, useSelector } from "react-redux";
import { isUserLoggedIn } from './actions';

function App() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    dispatch(isUserLoggedIn());
  }, [dispatch]);

  useEffect(() => {
    setIsLoggedIn(auth.authenticate);
    setUserRole(auth.user?.role || ''); // Use optional chaining to safely access role
  }, [auth.authenticate, auth.user]);

  return (
    <Router>
      <div className="App">
        <Header />
        <Routes>
          <Route path="/Header" element={isLoggedIn ? <Navigate to="/" /> : <CustomerDashboard />} />
          {/* Check if user is logged in and has the 'customer' role */}
          <Route path="/customer/dashboard" element={isLoggedIn && userRole === 'customer' ? <CustomerDashboard /> : <Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
