import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [customers, setCustomers] = useState([]);

  // Function to fetch all customer details from the backend
  const fetchCustomers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/users'); // Replace URL with your backend endpoint
      setCustomers(response.data.users); // Update state with fetched customer data
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  // Call fetchCustomers function when the component mounts
  useEffect(() => {
    fetchCustomers();
  }, []);

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li>Dashboard</li>
          <li>Orders</li>
          <li>Profile</li>
          <li>Settings</li>
          <li>Logout</li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome to the Customer Dashboard!</h1>
        <h2>Customer Details</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Address</th>
              {/* Add more table headers as needed */}
            </tr>
          </thead>
          <tbody>
            {customers.map((customer) => (
              <tr key={customer._id}>
                <td>{customer.name}</td>
                <td>{customer.email}</td>
                <td>{customer.address}</td>
                {/* Add more table data fields as needed */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CustomerDashboard;
