import React, { useEffect, useState } from 'react';
import { useNavigate} from 'react-router-dom'; // Import useHistory from React Router
import './style.css';
import axios from 'axios';


import { useDispatch, useSelector } from "react-redux";
import {signout} from '../../../actions/user.action';
const CustomerDashboard = () => {
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const history = useNavigate(); // Initialize useHistory

  const auth=useSelector(state=>state.auth);
  const dispatch=useDispatch();
  
  const logout=()=>{
    dispatch(signout());
  }
  

  const fetchCustomerDetails = async () => {
    try {
      const token = window.localStorage.getItem("token");
      const customerId = localStorage.getItem('id');
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data.user);
      setUpdatedUser(response.data.user);
    } catch (error) {
      if (error.response && error.response.status === 401) {
        
        console.error('Token expired or unauthorized:', error.response);
  
        alert('Your session has expired. Please login again.');
        
        dispatch(signout()); 
      } else {
       
        console.error('Error fetching customer details:', error.response);
      }
    }
  };

  const handleUpdate = async () => {
    try {
      const token =window.localStorage.getItem("token");
      const customerId = user._id.toString();
      console.log(token);
      await axios.put(`http://localhost:5000/users/${customerId}`, updatedUser, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      // Fetch updated user data after successful update
      await fetchCustomerDetails();
      // Exit edit mode
      setIsEditing(false);
    } catch (error) {
      if (error.response && error.response.status === 401) {
        
        console.error('Token expired or unauthorized:', error.response);
  
        alert('Your session has expired. Please login again.');
        
        dispatch(signout()); 
      } else {
       
        console.error('Error updating customer details:', error.response);
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    // Update the corresponding field in updatedUser state
    setUpdatedUser({ ...updatedUser, [name]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setProfileImage(file);
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <h4 align='center' className="name">{user ? user.name : 'Customer'}</h4>
          <li><button>Dashboard</button></li>
          <li><button>Orders</button></li>
          <li><button onClick={fetchCustomerDetails}>Profile</button></li>
          <li><button>Settings</button></li>
          <li><button onClick={logout}>Logout</button></li> 
        </ul>
      </div>
      <div className="container">
        <div className="customer-details">
          <h2>My Profile</h2>
          {user && (
            <div>
                    <p>
            <strong>Profile Photo:</strong>{' '}
            {isEditing ? (
              <input type="file" accept="image/*" onChange={handleFileChange} />
            ) : (
              'Display the profile photo here'
            )}
          </p>
              <p><strong>Name:</strong> {isEditing ? <input type="text" name="name" value={updatedUser.name} onChange={handleChange} /> : user.name}</p>
              <p><strong>DOB:</strong> {isEditing ? <input type="text" name="DOB" value={updatedUser.DOB} onChange={handleChange} /> : user.DOB}</p>
              <p><strong>Address:</strong> {isEditing ? <input type="text" name="address" value={updatedUser.address} onChange={handleChange} /> : user.address}</p>
              <p><strong>Phone Number:</strong> {isEditing ? <input type="text" name="phoneNumber" value={updatedUser.phoneNumber} onChange={handleChange} /> : user.phoneNumber}</p>
              <p><strong>Email:</strong> {isEditing ? <input type="text" name="email" value={updatedUser.email} onChange={handleChange} /> : user.email}</p>
              <p><strong>Username:</strong> {isEditing ? <input type="text" name="username" value={updatedUser.username} onChange={handleChange} /> : user.username}</p>
              
              {isEditing ? (
                <button className="saveButton" onClick={handleUpdate}>Save</button>
              ) : (
                <button className="editButton" onClick={() => setIsEditing(true)}>Edit</button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
