// Header.js
import React, { useState } from 'react';
import './style.css';
import { useDispatch } from 'react-redux';
import { login } from '../../actions';
import { useNavigat } from 'react-router-dom'; // Import useHistory hook

const Header = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const dispatch = useDispatch();
  const history = useNavigate(); // Use useHistory hook

  const handleLoginButtonClick = () => {
    setShowLoginForm(true);
  };

  const userLogin = () => {
    // Retrieve email and password values from the input fields
    const email = document.getElementById('email').value;
    const password = document.getElementById('password').value;

    // Simulate login success for demonstration
    // In real-world scenario, this should be replaced with actual login logic
    if (email === 'customer@example.com' && password === 'password') {
      // Redirect to customer dashboard upon successful login
      history.push('/customer-dashboard');
    } else {
      // Dispatch login action with email and password
      dispatch(login({ email, password }));
    }
  };

  // Rest of the component remains the same
};

export default Header;
