// index.js
import React, { useState } from 'react';
import './style.css';
import { useDispatch } from 'react-redux';
import { login } from '../../actions';

const Header = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const dispatch = useDispatch();

  const handleAdminLoginButtonClick = () => {
    // Redirect user to admin page running on port 4000
    window.location.href = 'http://localhost:4000/signin';
  };

  const userLogin = () => {
    // Retrieve email and password values from the input fields
    const email = document.getElementById('email').value;
    const password = document.getElementById('password').value;

    // Dispatch login action with email and password
    dispatch(login({ email, password }));
  };

  return (
    <div>
      <div className='headerBar'>
        <div className="dropdown">
          <button className="dropdown-toggle loginButton" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Login
          </button>
          <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
            <button className="dropdown-item" onClick={() => setShowLoginForm(true)}>Customer Login</button>
            <button className="dropdown-item" onClick={handleAdminLoginButtonClick}>Admin Login</button>
          </div>
        </div>
      </div>
      {showLoginForm && (
        <div className='loginFormContainer'>
          <div className='loginForm'>
            <h2>Login</h2>
            <input type='email' id='email' placeholder='email' className='inputField' />
            <input type='password' id='password' placeholder='Password' className='inputField' />
            <button className='loginSubmitButton' onClick={userLogin}>Login</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Header;
