import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [updatedUser, setUpdatedUser] = useState({});

  const fetchCustomerDetails = async () => {
    try {
      const token = localStorage.getItem('token');
      const customerId = localStorage.getItem('id');
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data.user);
    } catch (error) {
      console.error('Error fetching customer details:', error);
    }
  };

  useEffect(() => {
    fetchCustomerDetails();
  }, []);

  const handleUpdate = async () => {
    try {
      const token = localStorage.getItem('token');
      const customerId = localStorage.getItem('id');
      await axios.put(`http://localhost:5000/users/${customerId}`, updatedUser, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      // Fetch the updated user details after successful update
      fetchCustomerDetails();
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating customer details:', error);
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedUser({ ...updatedUser, [name]: value });
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li><button>Dashboard</button></li>
          <li><button>Orders</button></li>
          <li><button onClick={fetchCustomerDetails}>Profile</button></li>
          <li><button>Settings</button></li>
          <li><button>Logout</button></li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome, {user ? user.name : 'Customer'}!</h1>
        <h2>My Profile</h2>
        {user && !isEditing ? (
          <div>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>DOB:</strong> {user.DOB}</p>
            <p><strong>Address:</strong> {user.address}</p>
            <p><strong>Phone Number:</strong> {user.phoneNumber}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Username:</strong> {user.username}</p>
            <button onClick={() => setIsEditing(true)}>Update</button>
          </div>
        ) : user && isEditing ? (
          <div>
            <label>Name:</label>
            <input type="text" name="name" value={updatedUser.name || ''} onChange={handleChange} />
            <label>DOB:</label>
            <input type="text" name="DOB" value={updatedUser.DOB || ''} onChange={handleChange} />
            <label>Address:</label>
            <input type="text" name="address" value={updatedUser.address || ''} onChange={handleChange} />
            <label>Phone Number:</label>
            <input type="text" name="phoneNumber" value={updatedUser.phoneNumber || ''} onChange={handleChange} />
            <label>Email:</label>
            <input type="text" name="email" value={updatedUser.email || ''} onChange={handleChange} />
            <label>Username:</label>
            <input type="text" name="username" value={updatedUser.username || ''} onChange={handleChange} />
            <button onClick={handleUpdate}>Save</button>
            <button onClick={() => setIsEditing(false)}>Cancel</button>
          </div>
        ) : (
          <p>Loading customer details...</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
