import React, { useState } from 'react';
import './style.css';
import { useDispatch, useSelector } from 'react-redux';
import { login } from '../../actions';
import { useNavigate } from 'react-router-dom'; // Import useHistory hook
import {signup} from '../../actions';

const Header = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSignupForm, setShowSignupForm] = useState(false); // State for showing signup form
  const dispatch = useDispatch();
  const history = useNavigate(); // Initialize useHistory hook
  const auth = useSelector((state) => state.auth);
  

  const handleLoginButtonClick = () => {
    setShowLoginForm(true);
    setShowSignupForm(false); // Hide signup form when login form is displayed
  };

  const handleSignupButtonClick = () => {
    setShowSignupForm(true);
    setShowLoginForm(false); // Hide login form when signup form is displayed
  };

  const handleAdminLoginButtonClick = () => {
    // Redirect user to admin page running on port 4000
    window.location.href = 'http://localhost:4000/signin';
  };

  const userLogin = async () => {
    // Retrieve email and password values from the input fields
    const email = document.getElementById('email').value;
    const password = document.getElementById('password').value;

    // Dispatch login action with email and password
    await dispatch(login({ email, password }));

    // Check if the user is authenticated and has the role of 'customer'
    if (auth.authenticate && auth.user.role === 'customer') {
      
      // Redirect to the customer dashboard upon successful login
      history('/customer/dashboard');
    } else {
      // Handle other cases, such as displaying an error message
      console.log('Invalid credentials or user role');
    }

    setShowLoginForm(false);
  };

  // Function to handle signup
  const userSignup = async () => {
    // Retrieve signup form data
    const fullname = document.getElementById('fullname').value;
    const dob = document.getElementById('dob').value;
    const address = document.getElementById('address').value;
    const phoneNumber = document.getElementById('phoneNumber').value;
    const username = document.getElementById('username').value;
    const email = document.getElementById('signupEmail').value;
    const password = document.getElementById('signupPassword').value;
    e.preventDefault();
    const user={
      name,DOB,address,phoneNumber,username,email,password
    }
    dispatch(signup(user));
    // You can dispatch signup action here to handle signup process
    // Dispatch signup action with signup form data
    // await dispatch(signup({ fullname, dob, address, phoneNumber, username, email, password }));

    // For now, just console log the signup form data
    console.log({ fullname, dob, address, phoneNumber, username, email, password });

    // Close signup form after submission
    setShowSignupForm(false);
    
  };

  return (
    <div>
      <div className='headerBar'>
        <div className='spacer' /> {/* Spacer to push buttons to the right */}
        <button className='adminLoginButton' onClick={handleAdminLoginButtonClick}>
          Admin Login
        </button>
        <div style={{ marginRight: '10px' }} /> {/* Add space between buttons */}
        <button className='loginButton' onClick={handleLoginButtonClick}>
          Customer Login
        </button>
        <button className='signupButton' onClick={handleSignupButtonClick}>
          Sign Up
        </button>
      </div>
      {/* Login form */}
      {showLoginForm && (
        <div className='loginFormContainer'>
          <div className='loginForm'>
            <h2>Login</h2>
            <input type='email' id='email' placeholder='email' className='inputField' />
            <input type='password' id='password' placeholder='Password' className='inputField' />
            <button className='loginSubmitButton' onClick={userLogin}>Login</button>
          </div>
        </div>
      )}

      {/* Signup form */}
      {showSignupForm && (
        <div className='signupFormContainer'>
          <div className='signupForm'>
            <h2>Sign Up</h2>
            <input type='text' id='fullname' placeholder='Full Name' className='inputField' />
            <input type='date' id='dob' placeholder='Date of Birth' className='inputField' />
            <input type='text' id='address' placeholder='Address' className='inputField' />
            <input type='tel' id='phoneNumber' placeholder='Phone Number' className='inputField' />
            <input type='text' id='username' placeholder='Username' className='inputField' />
            <input type='email' id='signupEmail' placeholder='Email' className='inputField' />
            <input type='password' id='signupPassword' placeholder='Password' className='inputField' />
            <button className='signupSubmitButton' onClick={userSignup}>Sign Up</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Header;
