import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './components/Header';
import CustomerDashboard from './containers/HomePage/CustomerDashboard'; // Import CustomerDashboard component

import { useDispatch, useSelector } from "react-redux";
import { isUserLoggedIn } from './actions';

function App() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);
  const [showLoginForm, setShowLoginForm] = useState(false);

  useEffect(() => {
    if (!auth.authenticate) {
      dispatch(isUserLoggedIn());
    }
  }, [dispatch, auth.authenticate]);

  const handleLoginButtonClick = () => {
    setShowLoginForm(true);
  };

  return (
    <Router>
      <div className="App">
        <Header handleLoginButtonClick={handleLoginButtonClick} />
        <Routes>
          {/* Display login form if not authenticated */}
          {showLoginForm && !auth.authenticate && <Route path="/" element={<Heder />} />}

          {/* Redirect to CustomerDashboard if authenticated */}
          {auth.authenticate && <Route path="/" element={<Navigate to="/customer/dashboard" />} />}

          {/* Customer dashboard route */}
          {auth.authenticate && <Route path="/customer/dashboard" element={<CustomerDashboard />} />}

          {/* Other routes */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
