import React, { useEffect, useState } from 'react';
import './style.css';
import axios from 'axios';

const CustomerDashboard = () => {
  const [customer, setCustomer] = useState(null);

  const fetchCustomerDetails = async () => {
    try {
      const token = localStorage.getItem('token');
      const customerId = localStorage.getItem('id');
      if (!customerId) {
        throw new Error('Token or customer ID not found in localStorage');
      }
      const response = await axios.get(`http://localhost:5000/users/${customerId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('Customer details response:', response.data); // Log response data
      setCustomer(response.data.user); // Assuming response contains 'user' object
    } catch (error) {
      console.error('Error fetching customer details:', error);
    }
  };

  useEffect(() => {
    fetchCustomerDetails();
  }, []);

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul>
          <li>Dashboard</li>
          <li>Orders</li>
          <li>Profile</li>
          <li>Settings</li>
          <li>Logout</li>
        </ul>
      </div>
      <div className="container">
        <h1>Welcome to the Customer Dashboard!</h1>
        <h2>Customer Details</h2>
        {customer ? (
          <div>
            <p><strong>Name:</strong> {customer.name}</p>
            <p><strong>Email:</strong> {customer.email}</p>
            <p><strong>Address:</strong> {customer.address}</p>
            {/* Add more customer details as needed */}
          </div>
        ) : (
          <p>Loading customer details...</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
