import { authConstants } from "./constants";
import axios from "axios";

export const login = (user) => {
  console.log(user);
  return async (dispatch) => {
    dispatch({ type: authConstants.LOGIN_REQUEST });

    const res = await axios.post("http://Localhost:5000/users/admin/signin", {
      ...user,
    });

    if (res.status === 200) {
      const { token, user } = res.data;
      localStorage.setItem("token", token);
      localStorage.setItem("user", JSON.stringify(user));
      dispatch({
        type: authConstants.LOGIN_SUCCESS,
        payload: {
          token,
          user,
        },
      });
    } else {
      if (res.status === 400) {
        dispatch({
          type: authConstants.LOGIN_FAILURE,
          payload: { error: res.data.error },
        });
      }
    }
  };
};

export const isUserLoggedIn = () => {
  return async (dispatch) => {
    const token = localStorage.getItem("token");
    if (token) {
      const user = JSON.parse(localStorage.getItem("user"));
      dispatch({
        type: authConstants.LOGIN_SUCCESS,
        payload: {
          token,
          user,
        },
      });
    } else {
      dispatch({
        type: authConstants.LOGIN_FAILURE,
        payload: { error: "Failed to login" },
      });
    }
  };
};

export const signout = () => {
  return async dispatch => {
    dispatch({ type: authConstants.LOGOUT_REQUEST });
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }
      
      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      };
      
      console.log('Request Headers:', config.headers); // Log the request headers
      
      const res = await axios.post("http://Localhost:5000/users/admin/signout", {}, config);
      
      if (res.status === 200) {
        localStorage.clear();
        dispatch({ type: authConstants.LOGOUT_SUCCESS });
      } else {
        throw new Error('Failed to sign out');
      }
    } catch (error) {
      console.error('Error during signout:', error);
      dispatch({
        type: authConstants.LOGOUT_FAILURE,
        payload: { error: error.message }
      });
    }
  };
};
